'use stricts';

import React from 'react';
import Highcharts from 'highcharts';

export default class Hicharts  extends React.Component {
  componentDidMount() {
    Highcharts.chart(this.refs.chart, this.props.options);
  }

  componentDidUpdate() {
    Highcharts.chart(this.refs.chart, this.props.options);
  }

  render() {
    return (
      <div ref="chart"></div>
    );
  }
}
