/**
 * Hicharts
 * React highcharts, create charts in React.
 * @author walkerbe <hiwangchi@gmail.com>
 */

'use stricts';

var React = require('react');
var Highcharts = require('highcharts');

var Hicharts = React.createClass({
  displayName: 'Hicharts',

  componentDidMount: function componentDidMount() {
    Highcharts.chart(this.refs.chart, this.props.options);
  },

  componentDidUpdate: function componentDidUpdate() {
    Highcharts.chart(this.refs.chart, this.props.options);
  },

  render: function render() {
    return React.createElement('div', { ref: 'chart' });
  }
});

module.exports = Hicharts;
